const res = new Array();
const count = 0;
const s = cts.search("dog");
for (const item of s) {
  count++;
  if (count > 3) { break;}
  res.push(cts.confidence(item));
  res.push(xdmp.nodeUri(item));
}
res;

// returns an array of the confidence numbers and the URI for the
// first 3 items returned by the search

