    at "/MarkLogic/admin.xqy";

let $config := admin:get-configuration()
let $groupid := admin:group-get-id($config, "Default")
return
admin:appserver-get-max-query-time-limit($config,
           admin:appserver-get-id($config, $groupid, "myAppServer"))

